/**
 * Filters unique items from an array. Accepts both primitives and objects.
 * @param {Array} array - Target array
 * @param {string} [property] - Property to be accessed if handling an object array.
 * @returns {Array} New array containing the unique items.
 */
module.exports = function (array, property) {
    let index = [];
    return array.filter(function (item) {
        var key = property ? item[property] : item;
        return index.indexOf(key) >= 0 ? false : index.push(key);
    });
}

